def read_mp3_links_from_secret_file():
    links = {}
    remote_path = '/etc/secrets/LINKS'  # absolute path for secret file on Render
    local_path = '.secrets'  # local dev environment
    for path in (remote_path, local_path):
        try:
            with open(path) as file:
                lines = file.readlines()
                for line in lines:
                    episode, mp3_link = line.split(': ')
                    links[episode] = f'https://drive.google.com/file/d/{mp3_link.strip()}'
        except FileNotFoundError:
            pass
    return links


